mapper.select:
select btgi.batch_id, bi.car_no, bi.id as car_id, YEAR (bi2.start_time) as start_year, YEAR (bi2.end_time) AS end_year, DAYOFWEEK(bi2.start_time) as start_week, DAYOFWEEK(bi2.end_time) as end_week, bi2.start_time, bi2.end_time, DATE_FORMAT(bi2.start_time,'%m/%d') as start_date_str, DATE_FORMAT(bi2.end_time, '%m/%d') as end_date_str, bi2.status as batch_status, p.assemble_time, p.departure_time, p.name as product_name, ali.address, ci.city_name, COUNT(ti.age_type) as total_num, SUM(case WHEN ti.age_type = '成人' THEN 1 ELSE 0 END) as adult_num, SUM(case WHEN ti.age_type = '儿童' THEN 1 ELSE 0 END) as child_num
from bus_tour_guide_info btgi left join bus_info bi on btgi.car_id = bi.id left join bus_tour_info bti on bti.car_id = bi.id and bti.is_deleted = 0 left join batch_info bi2 on bi2.id = btgi.batch_id left join product p on p.id = bi2.product_id left join assemble_location_info ali on ali.id = p.place_to_start_id left join city_info ci on ci.id = ali.city_id left join order_item oi on oi.batch_id = bi2.id left join trip_info ti on ti.id = oi.trip_id and bti.tour_id = ti.id and oi.is_deleted = 0
and btgi.batch_id = #{batchId}
and btgi.tour_guide_id = #{tourGuideId}
and btgi.is_deleted = 0
group by btgi.batch_id, bi.id

select ti.id as trip_id, oi.id as order_id, oi2.id as order_item_id, ti.name , ti.sex, ti.age_type, oi2.order_status, oi2.check_status, ti.id_card_no as card_no, ti.phone
from bus_tour_info bti left join trip_info ti on ti.id = bti.tour_id left join order_info oi on oi.batch_id = bti.batch_id and oi.is_deleted = 0 left join order_item oi2 on oi2.order_id = oi.id and oi2.trip_id = ti.id and oi2.is_deleted = 0
and bti.batch_id = #{batchId}
and bti.car_id = #{carId}
and oi2.check_status = #{checkStatus}
and oi2.check_status in (0,1)
and ti.name like concat('%', #{name}, '%')
and bti.is_deleted = 0
group by ti.id, oi2.id, oi.id order by oi2.check_status

mapper.update:
update order_item


        order_status = #{orderStatus},


        check_status = #{checkStatus},


where id = #{orderItemId}
and batch_id = #{batchId}
and trip_id = #{tripId}
and order_id = #{orderId}

update order_info
set order_status = #{orderStatus}
where id = #{orderId}
  and is_deleted = 0

mapper.select:
select oi.*
from order_item oi
where oi.batch_id = #{batchId}
  and oi.order_id = #{orderId}
  and oi.is_deleted = 0
  and oi.order_status = 1
